func fibonacci(_ n: Int) -> Int {
    if n <= 0 {
        return 0
    } else if n == 1 {
        return 1
    } else {
        var fibArray = [0, 1]
        for i in 2...n {
            let fibNumber = fibArray[i-1] + fibArray[i-2]
            fibArray.append(fibNumber)
        }
        return fibArray[n]
    }
}

