def is_prime(n):
    # Check if the number is less than 2, as 0 and 1 are not prime
    if n < 2:
        return False

    # Check for divisibility from 2 to the square root of the number
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False

    return True

