#include <cmath>
using namespace std;

int binaryToDecimal(int binary) {
    int decimal = 0, base = 0;

    while (binary != 0) {
        int rem = binary % 10;
        decimal += rem * pow(2, base);
        binary /= 10;
        base++;
    }

    return decimal;
}

